--------------------------------------------------------------------------
#include &lt;iostream&gt;
#include &lt;list&gt;
#include &lt;functional&gt;

typedef int u32;

class CIntCompare : public binary_function&lt;u32,u32,bool&gt;
{
public:
	u32 map_size;

	bool operator()(const u32& a, const u32& b) const {
                cout &lt;&lt; a &lt;&lt; &quot; vs &quot; &lt;&lt; b &lt;&lt; endl;
		return a &lt; b;
	}
};


void main(void)
{
        list&lt;u32&gt; lista;
	CIntCompare comp;

	lista.push_back( 10 );
	lista.push_back( 200 );
	lista.push_back( 20 );

	lista.sort( comp );

	list&lt;int&gt;::iterator it;

	for(it = lista.begin(); it != lista.end(); ++it)
		cout &lt;&lt; (*it) &lt;&lt; endl;
}
